import React, { Component, Fragment } from "react"
import API from "../utils/API"

const styles = {
    image: {
        width: 300,
        padding: "0 15px"
    },
    buttons: {
        paddingTop: 15,
    },
    button: {
        margin: "0 15px"
    }
}

class Discover extends Component {

    state = {
        currentDog: "",
        friendCount: 0
    }

    // only available in places that start with class Discover extends Component {
    componentDidMount() {
        this.getNextDog();
    }

    getNextDog = () => {
        API.showDog()
            .then(res => this.setState({ currentDog: res.data.message }))
            .catch(err => console.log(err))
    }

    render() {
        return (
            <Fragment>
                <h1> Discover </h1>
                <div>
                    <div className="alert alert-secondary" role="alert">
                        We will come back to this
</div>
                    <img src={this.state.currentDog} alt="Dog" style={styles.image} />
                    <p style={styles.buttons}>
                        <button type="button" style={styles.button} className="btn btn-lg btn-success">Lets be Friends</button>
                        <button type="button" style={styles.button} className="btn btn-secondary">I dont like you</button>
                    </p>
                </div>
            </Fragment>
        )
    }
} // end of class tag

export default Discover;
