import React, { Component, Fragment } from "react"
import API from "../utils/API"

const styles = {
    image: {
        width: 300,
    }
}

class Discover extends Component {

    state = {
        currentDog: "",
        friendCount: 0
    }

    // only available in places that start with class Discover extends Component {
  componentDidMount() {
      this.getNextDog();
  }  

    getNextDog = () => {
        API.showDog()
        .then(res => this.setState({ currentDog: res.data.message}))
        .catch(err => console.log(err))
    }

    render() {
        return (
            <Fragment>
            <h1> Discover </h1>
            <div>
            <img src={this.state.currentDog} alt="Dog" style={styles.image}/>    
            <p>
            <button type="button" class="btn btn-lg btn-success">Lets be Friends</button>
            </p>
            </div>
            </Fragment>
        )
    }
} // end of class tag

export default Discover;
